import React, { Children } from 'react'
import { useSelector } from 'react-redux'
import { Route,useNavigate ,Redirect} from 'react-router-dom'
import { useLocation } from 'react-router-dom';
import { Navigate } from 'react-router-dom';




const UserRoutes = ({auth,component:Component, ...rest}) => {
    const {user}=useSelector((state)=> ({...state}))   //this how get the user from redux state
    const location=useLocation()
    const navigate=useNavigate()
   return (
       <Route {...rest} render={  (props)=>{
           if(user && user.token) return <Component {...props}/>
           if(!(user && user.token) ) return <Navigate to={{path:'/',state:{from:props.location}}}/>
       }  } />

   );
        
        
        
    
  };
  


export default UserRoutes
