import React from 'react'
import { useState,useEffect } from 'react'
import { useSelector } from 'react-redux'
import { createCategory,getCategories } from '../../../functions/category'
import { toast } from 'react-toastify'
import { DeleteOutlined ,EditOutlined } from '@ant-design/icons';

import AdminNav from '../../../components/nav/AdminNav'
import { Link } from 'react-router-dom'

const CreateCategory = () => {
    const[name,setname]=useState('')
    const[categories,setcategories]=useState([]);
    const {user}=useSelector(state=>({...state}))
    useEffect(()=>{
        listCategory()
    },[])

     const listCategory=()=>getCategories()
     .then((res)=>setcategories(res.data))
     .catch(err=>console.log(err))
     
    const handleClick=(e)=>{
        e.preventDefault()
        console.log("clicked")
        createCategory({name},user.token)
        .then((e)=>{
            
            toast.success('Category created')
        })
        .catch(err=>{
            if(err.response.status===400) toast.error(err.response.data)
            console.log(err)
        })
            
    

    }
   
     
    
        return (
        <div className='container-fluid' >
            <div className='row'>
           
                <div className='col'  style={
                    {margin: '0px 0px 0px 200px'}
                }>
                   
                <div className='col' ><h2>Create Category</h2></div>
                  <form onSubmit={handleClick}>
                      <input type="text" 
                      className='form-control'
                      onChange={(e)=>setname(e.target.value)}
                      value={name}
                      autoFocus
                      required
                      
                      placeholder='Enter name of category'
                      style={{margin:'20px 0px 0px 0px',width:'1000px'}}>
                        
                        
                      </input>
                      <button type='submit' value="submit"className='btn btn-primary' style={{margin:'20px 0px 0px 0px'}}>Save</button>
                  </form>
                  <div >{categories.map((c)=>(
                <div className='alert alert-secondary' style={{margin:'10px 0px 0px 0px',width:'1000px'}} key={c._id} >
                    {c.name}
                    <span className='btn btn-sm  float-end' >
                       <DeleteOutlined/>
                    </span>
                    <span className='btn btn-sm  float-end' >
                       <Link to={`/admin/category/${c.slug}`}><EditOutlined/></Link>
                    </span>

                </div>

            )

            )}
            </div>
                 
                </div>
                
                
                
            </div>
            
        </div>
        
    )
}

export default CreateCategory
