import React from 'react'
import ModalImage from 'react-modal-image'
import laptop from '../../images/laptop.png'
import { useSelector,useDispatch } from 'react-redux'

const handleQuantityChange=(e)=>{
    const dispatch=useDispatch()
    let cart=[];
    if(typeof window!=='undefined'){
        if(localStorage.getItem('cart')){
            cart=JSON.parse(localStorage.getItem('cart'))
        }
        cart.map((product,i)=>{
           if(product._id==p._id){
               cart[i].count=e.target.value
           }
        })
        localStorage.setItem('cart',JSON.stringify(cart))
        dispatch({
            type:'ADD_TO_CART',
            payload: cart,
        })
    }
}

const ProductCardCheckOut = ({p}) => {
  return (
    <tbody>
        <tr>
            <td>
                <div style={{width:'120px' ,height:'auto'}}>
                    {p.images.length ? (<ModalImage small={p.images[0].url} large={p.images[0].url}/>):(
                        <ModalImage small={laptop} large={laptop}/>
                    )}
                </div>
            </td>
            <td>{p.title}</td>
            <td>{p.price}</td>
            <td>{p.brand}</td>
            <td>{p.color}</td>
            <td className='text-center'>
                  <input type='number' className='form-control' value={p.count} onChange={handleQuantityChange}></input>      
            </td>
            <td>Shipping Icon</td>
            <td>Delete Icon</td>
        </tr>
    </tbody>
  )
}

export default ProductCardCheckOut
