import React from 'react'
import { getProducts, getProdutByCount } from '../functions/product';
import { useState,useEffect } from 'react';
import ProductCard from '../components/cards/ProductCard';
import Jumbotron from '../components/cards/Jumbotron';
import LoadingCard from '../components/cards/LoadingCard';
const Home = () => {
    const[products,setProducts]=useState([])
    const[loading,setLoading] =useState(false)
    useEffect(()=>{
        loadAllproducts()
    },[])
    const loadAllproducts=()=>{
        setLoading(true)
        getProducts('createdAt','desc',3)
        .then(res=>{
            setProducts(res.data);
            console.log(products)

        })
        .catch((err)=>{
            console.log(err)
        })
        setLoading(false)
        
    }
    return(
        <>
     <div className="mt-4 p-5 bg-light text-danger rounded h1 font-weight-bold text-center">
        

        <Jumbotron text={['Latest Products','New Arrivals','Best Sellers']}/>
    </div> 
     <h3 className='mt-4 mb-5 p-5 bg-light text-black rounded text-center  font-weight-italic'> New Arrivals</h3> 
    <div className='container'>
        {loading ?<LoadingCard/>:<div className='row'>
            {products.map((product)=>(
                <div key={product._id} className="col-4">
                    <ProductCard product={product}/>
                </div>
            ))}
        </div>}
    </div>
    </>
    );
      
       
    
}

export default Home;
