import React from 'react'
import AdminNav from '../../components/nav/AdminNav'
import { useEffect,useState } from 'react'
import { getProdutByCount } from '../../functions/product'

import AdminProductCard from '../../components/cards/AdminProductCard'

const AdminDashboard = () => {
    const[products,setproducts]=useState([])
    useEffect(() => {
        setTimeout(() => {
    //I want to run the interval here, but it will only run once 
    //because of no dependencies. If i populate the dependencies, 
    //setTimeout will run more than once.
    getProdutByCount(10)
    .then((res)=>{
       
        setproducts(res.data)
         console.log(products)
        

    })
    .catch((err)=>{
       
        console.log(err);
    })
    }, Math.random() * 1000);
      }, []);
     
          
   

   

    return (
        <div className='container-fluid'>
            <div className='row'>
                <div className='col'>
                  <AdminNav/>
                </div>
               <div className='col-10'> 
               <h4 >All Products </h4>
                 <div className='row'>{JSON.stringify(products)}
                    
                     {products.map((product)=>{
                         <div key={product._id} className="col">
                             {product}
                             <AdminProductCard product={product}/>
                         </div>
                     })}
                 </div>

                 </div>
                
                
            </div>
        </div>
    )
}

export default AdminDashboard
