import React from 'react'
import ModalImage from 'react-modal-image'
import laptop from '../../images/laptop.png'
import { useSelector,useDispatch } from 'react-redux'
import { toast } from 'react-toastify'
import {CheckCircleOutlined,CloseCircleOutlined,CloseOutlined} from '@ant-design/icons'



const ProductCardCheckOut = ({p}) => {
    const dispatch=useDispatch()
    const handleQuantityChange=(e)=>{
        let count=e.target.value < 1?1:e.target.value
        if(count>p.quantity){
            toast.error(`Max available quantity: ${p.quantity}`)
            return ;
        }

        let cart=[];
        if(typeof window!=='undefined'){
            if(localStorage.getItem('cart')){
                cart=JSON.parse(localStorage.getItem('cart'))
            }
            cart.map((product,i)=>{
               if(product._id==p._id){
                   cart[i].count=count
               }
            })
            localStorage.setItem('cart',JSON.stringify(cart))
            dispatch({
                type:'ADD_TO_CART',
                payload: cart,
            })
        }
    }

    const handleRemove=()=>{
        console.log(p._id);
    }

  return (
    <tbody>
        <tr>
            <td>
                <div style={{width:'120px' ,height:'auto'}}>
                    {p.images.length ? (<ModalImage small={p.images[0].url} large={p.images[0].url}/>):(
                        <ModalImage small={laptop} large={laptop}/>
                    )}
                </div>
            </td>
            <td>{p.title}</td>
            <td>{p.price}</td>
            <td>{p.brand}</td>
            <td>{p.color}</td>
            <td className='text-center'>
                  <input type='number' className='form-control' value={p.count} onChange={handleQuantityChange}></input>      
            </td>
            <td>
                {p.shipping==="Yes"? <CheckCircleOutlined className='text-success'/>:<CloseCircleOutlined className='text-danger'/>}
            </td>
            <td>
                <CloseOutlined onClick={handleRemove} className='text-danger pointer'/>
            </td>
        </tr>
    </tbody>
  )
}

export default ProductCardCheckOut
