import React from 'react'
import { useState,useEffect } from 'react'
import { useSelector } from 'react-redux'
import { createCategory,getCategories } from '../../../functions/category'
import { toast } from 'react-toastify'

import AdminNav from '../../../components/nav/AdminNav'

const CreateCategory = () => {
    const[name,setname]=useState('')
    const[categories,setcategories]=useState([]);
    const {user}=useSelector(state=>({...state}))
    useEffect(()=>{
        listCategory()
    },[])

     const listCategory=()=>getCategories()
     .then((res)=>setcategories(res.data))
     .catch(err=>console.log(err))
     
    const handleClick=(e)=>{
        e.preventDefault()
        console.log("clicked")
        createCategory({name},user.token)
        .then((e)=>{
            
            toast.success('Category created')
        })
        .catch(err=>{
            if(err.response.status===400) toast.error(err.response.data)
            console.log(err)
        })
            
    

    }
   
     
    
        return (
        <div className='container-fluid' >
            <div className='row'>
                <div className='col-mid-2'  style={
                    {margin: '20px 0px 0px 400px'}
                }>
                <div className='col' style={{margin:"20px 0px 0px 400px"}}><h2>Create Category</h2></div>
                  <form onSubmit={handleClick}>
                      <input type="text" 
                      className='form-control'
                      onChange={(e)=>setname(e.target.value)}
                      value={name}
                      autoFocus
                      required
                      style={{margin:"20px 0px 0px 400px",width:"600px"}}
                      placeholder='Enter name of category'>
                        
                          
                      </input>
                      <button type='submit' value="submit"className='btn btn-primary' style={{margin:"20px 0px 0px 400px"}}>Save</button>
                  </form>
                 {categories.length}
                </div>
                
                
            </div>
        </div>
    )
}

export default CreateCategory
