import React from 'react'
import { useState,useEffect } from 'react'
import { useSelector,useDispatch } from 'react-redux'
import { getUserCart,emptyCart } from '../functions/user'
import {toast} from 'react-toastify'


const Checkout = () => {

    const dispatch=useDispatch()
    const {user}=useSelector((state)=>({...state}))
    const[products,setProducts]=useState([])
    const[total,setTotal]=useState(0)

    useEffect(()=>{
        getUserCart(user.token).then((res)=>{
            console.log('user cart res',JSON.stringify(res.data,null,4))
            setProducts(res.data.products)
            setTotal(res.data.cartTotal)
        })
    },[])

    const empty= () => {
        //
        if(typeof window!=='undefined'){
            localStorage.removeItem('cart')
        }

        dispatch({
            type:'ADD_TO_CART',
            payload:[]
        });

        emptyCart(user.token).then((res)=>{
            setProducts([])
            setTotal(0)
            toast.success('Cart is empty. Continue Shopping')
            
        })
    }

    return (
        <div className='container-fluid'>
            <div className='row'>
                
                <div className='col-6'>
                    <h4>Delivery Address</h4>
                    <br />
                    <br />
                    textarea
                    <button className='btn btn-success mt-2'  >Save</button>
                    <hr />
                    <h4>Got Coupon?</h4>
                    <br />
                    <br />
                </div>
                <div className='col-6'>
                    <h4>Order Summary</h4>
                    
                   
                    <hr />
                    <p>Products {products.length}</p>
                    <hr />
                    {products.map((p,i)=>(
                        <div key={i}>
                            <p>{p.product.title}  ({p.color}) x {p.count}={''}
                            {p.product.price*p.count}</p>
                        </div>
                    ))}
                    <hr />
                    <p>Cart total :{`Rs${total}`}</p>

                    <div className='row'>
                        <div className='col-6'>
                            <button className='btn btn-primary mt-2'>Place Order</button>
                        </div>
                        <div className='col-6'>
                            <button 
                            disabled={!products.length}
                            onClick={empty}
                             className='btn btn-primary'>Empty Cart</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Checkout
