import React ,{useState}from 'react'
import { getCategory } from '../../functions/category'
import { useEffect } from 'react'
import { useParams } from 'react-router-dom'
import ProductCard from '../../components/cards/ProductCard'
import CategoryList from '../../components/category/CategoryList'

const CategoryHome = () => {
    const [categories,setCategories]=useState({})
    const[products,setProducts]=useState([])
    const[loading,setLoading]=useState(false)

    let params=useParams()
    const{slug}=params
    useEffect(()=>{
        setLoading(true)
        getCategory(slug)
        .then((res)=>{
            setCategories(res.data.category)
            console.log(JSON.stringify(res.data),null,4)
            setProducts(res.data.products)
            setLoading(false)
        })
    },[])

  return (
    <div className='container-fluid'>
        {loading ?(<h4 className='mt p-2 bg-light text-black rounded h1  text-center'>Loading...</h4>):(
            <h4 className='mt-5 mb-5 p-4 bg-light text-black rounded text-center  font-weight-italic'>{products.length} Products in "{categories.name}" category</h4>
        )}
    </div>
  )
}

export default CategoryHome
