import React,{useState,useEffect} from 'react'
import { getProdutByCount,fetchSearchFilter } from '../functions/product'
import { useSelector,useDispatch } from 'react-redux'
import ProductCard from '../components/cards/ProductCard'
import {Menu,Slider,Checkbox} from 'antd'
import {DollarOutlined,DownSquareOutlined} from '@ant-design/icons'
import { getCategories } from '../functions/category'

const Shop = () => {
    const [products,setProducts]=useState([])
    const[loading,setLoading]=useState(false)
    const[price,setPrice]=useState([0,0])
    const[categories,setCategories]=useState([])
    const[ok,setOk]=useState(false)
    const {search}=useSelector((state)=>({...state}))
    

    const{text}=search
    let dispatch=useDispatch()

   useEffect(()=>{
    loadProducts()
    getCategories()
    .then(res=>{
        setCategories(res.data)
        console.log(res.data)
    })
   },[])
//    {for search query}
   useEffect(()=>{
       const delayed=setTimeout(()=>{
        fetchProducts({query:text})
       },300)
    return ()=>clearTimeout(delayed)
   },[text])

//    {default shop page}
   const loadProducts=()=>{
       getProdutByCount(10).then((res)=>{
           setLoading(true)
           
           setProducts(res.data)
         
           setLoading(false)
       })
   }
//    {universal function for filter search }
   const fetchProducts=(arg)=>{
    fetchSearchFilter(arg)
    .then(res=>{
        setProducts(res.data)
    })
   }
//    {for price filtering}
   useEffect(()=>{
       fetchProducts({price});

   },[ok])
   const handleSlider=(value)=>{
       dispatch({
           type:'SEARCH_QUERY',
           payload:{text:''}
       })
       setPrice(value)
       setTimeout(()=>{
           setOk(!ok)
       },300)
   }

  

   const showCategories=()=>{
       <div>Hey</div>
       categories.map((c)=>(
           <div key={c._id}>
               <Checkbox className='pl-4 pr-4 pb-2 ' value={c._id} name='category'>
                    {c.name}
               </Checkbox>

           </div>
       ))
   }
   const{SubMenu}=Menu

  return (
    <div className='container-fluid'>
        <div className='row'>
            <div className='col-3'>
                <h4>Search/Filter</h4>
                <hr/>
                <Menu defaultOpenKeys={['1','2']} mode='inline'>
                    <SubMenu key='1' title={<span className='h6'><DollarOutlined/> Price</span>}>
                        <div>
                            <Slider className='ml-4 mr-4'
                            tipFormatter={(v)=>`Rs${v}`}
                            range
                            value={price}
                            onChange={handleSlider}
                            max='150000'
                            />
                        </div>
                    </SubMenu>
                    <SubMenu key='2' title={<span className='h6'><DownSquareOutlined/> Catgories</span>}>
                        <div>
                           {JSON.stringify(categories)}
                        </div>
                    </SubMenu>
                </Menu> 

            </div>
            <div className='col-9'>
                {loading?(<h4 className='text-danger'>Loading..</h4>):(<h4 className='text-danger'>Products</h4>)}

                {products.length<1 && <p>No Products found</p>}

                <div className='row'>
                    {products.map((p)=>(
                        <div key={p._id} className='col-4 mt-5'>
                            <ProductCard product={p}/>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    </div>
  )
}

export default Shop
