import React from "react";
import {Card,Tabs} from 'antd'
import {Link} from 'react-router-dom'
import {HeartOutlined,ShoppingCartOutlined} from '@ant-design/icons'
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';
import laptop from "../../images/laptop.png"
import ProductsListItems from './ProductsListItems'
import StarRating from 'react-star-ratings'
import RatingModal from "../modal/RatingModal";
import { useState } from "react";

const{Meta}=Card
const{TabPane}=Tabs
const SingleProduct=({product,onStarClick})=>{
    const{title,images,description,_id}=product
    const[star,setStar]=useState(0)
    return(
        <>
        
       
        <div class="container">
  <div class="row">
    <div class="col">
    {images && images.length ?(<Carousel showArrows={true} autoPlay infiniteLoop>
            {images && images.map((i)=><img src={i.url} key={i.public_id}/>)}
        </Carousel>):(<Card cover={<img src={images && images.length? images[0].url:laptop} style={{objectFit:"cover"}}
      
      className="p-1"/>}></Card>)}
      <Tabs type="card">
          <TabPane key="1" tab="Description">
              {description && description}
          </TabPane>
          <TabPane key="2" tab="More">
            Call us
          </TabPane>
      </Tabs>
    </div>

    <div class="col">
        <h1 className="bg-info p-3 ">{title}</h1>
       

    <Card
          actions={[
            <>
            <ShoppingCartOutlined className="text-success"/><br/>
            Add to Cart
            </>,
            <Link to='/'>
                <HeartOutlined className="text-info" /><br/> Add to Wishlist
            </Link>,
             <RatingModal>
             <StarRating  
                 name={_id}
                 numberOfStars={5}
                 rating={star}
                 changeRating={onStarClick
                 }
                 isSelectable={true}
                 starRatedColor="red"
                 star={star}
             />
             </RatingModal>
        ]}>
           
           <ProductsListItems product={product}/>
          </Card>
    </div>
    
  </div>
</div>
        </>
    )
}
export default SingleProduct
