import React from 'react'
import {Card,Skeleton} from 'antd'
import laptop from "../../images/laptop.png"
import {EyeOutlined,ShoppingCartOutlined} from  '@ant-design/icons'
import { Link } from "react-router-dom";


const{Meta}=Card
const ProductCard = ({product}) => {
    const{title,description,images,slug,price}=product;
  return (
    <>
    
      
    <Card cover={<img src={images && images.length? images[0].url:laptop} style={{height:'250px',objectFit:"cover"}}
      
    className="p-1"/>}
    actions={[
      <Link to={`/product/${slug}`}><EyeOutlined className="text-warning"/><br/>View Product </Link>,
    <>
      <ShoppingCartOutlined className="text-danger" /><br/>Add to Cart
      </>,
    ]}
    className="mb-2">
          
        <Meta title={`${title}-Rs${price}`} description={`${description && description.substring(0,40)}...`}/>
    </Card> 
    </>
  )
}

export default ProductCard
