import axios from 'axios'
const cors=require('cors')
export const getCategories=async ()=>{
    await axios.get("http://localhost:8000/api/categories")

}
export const getCategory=async (slug)=>{
    return await axios.get(`http://localhost:8000/api/category/${slug}`)

}
export const deleteCategory=async(slug,authtoken )=>{
    return await axios.delete(`http://localhost:8000/api/category/${slug}`,{headers: {'Access-Control-Allow-Origin': true},
    mode: 'cors',authtoken})


}
export const createCategory=async(category,authtoken)=>{
    return await axios.post(`http://localhost:8000/api/category`,{category},{headers:{authtoken,'Access-Control-Allow-Origin': '*',mode:'cors'}
    })


}
export const updateCategory=async(slug,category,authtoken)=>{
    return await axios.put(`http://localhost:800/api/category/${slug}`,category,{headers:authtoken})

}
